edit.action:
// ajaxlike
if($config['ajaxlike_enable'])
{
	include($phpbb_root_path . 'includes/functions_ajaxlike.' . $phpEx);
	$ajaxlike_action	= request_var('ajaxlike_action', '');
	$ajaxlike_data		= request_var('ajaxlike_data', '');
	
	if($ajaxlike_action	!= '')
	{

		// do not trust input!
		$sql = 'SELECT forum_id
			FROM ' . TOPICS_TABLE . "
			WHERE topic_id = $topic_id";
		$result = $db->sql_query($sql);
		$forum_id = (int) $db->sql_fetchfield('forum_id');
		$db->sql_freeresult($result);

		if (!$forum_id)
		{
			ajaxlike_die('Invalid request!');
		}
		
		if($ajaxlike_action	== 'fulllist')
		{
			ajaxlike_die(get_fulllist($post_id));
		} else {

		if (($auth->acl_get('u_ajaxlike_mod')) && ($auth->acl_get('f_ajaxlike_mod', $forum_id)) && ($user->data['user_id'] != ANONYMOUS))
		{
		
			if($ajaxlike_action	== 'like')
			{
				ajaxlike_die(ajaxlike_like_post($post_id));
			}
		
			if(($ajaxlike_action	== 'unlike') && $config['ajaxlike_allow_unlike'])
			{
				ajaxlike_die(ajaxlike_unlike_post($post_id));
			}
		
		} else {
			ajaxlike_die("Access Denied!");
		}
		
		}
		
	}
}
// ajaxlike

// ajaxlike
'acl_f_ajaxlike_mod'=> array('lang' => 'Can like posts in forum', 'cat' => 'misc'),
// ajaxlike
